import 'dart:convert';

import 'package:flutter/src/painting/image_provider.dart';
import 'package:flutter/src/widgets/framework.dart';
import 'package:flutter_ume/core/pluggable.dart';
import 'package:flutter_ume/flutter_ume.dart';
import 'package:flutter_ume/kits/box_kit/env/service_env_provider.dart';

import '../box_kit.dart';

const envIconData =
    r'';

// const envIconData = r'';
final envIconBytes = base64Decode(envIconData);

class ServiceEnvPlugin implements Pluggable {

  late List<HzBoxBaseProvider> _providers;
  final Function(bool) onFinish;
  ServiceEnvPlugin({required this.onFinish}){
    _providers = [ServiceEnvProvider()];
    onFinish.call(true);
  }

  @override
  Widget? buildWidget(BuildContext? context) {
    return HzBoxConfigWidget(
      providers: _providers,
      title: displayName,
      onSelected: (HzBoxBaseProvider<dynamic> provider, HzBoxConfigItem<dynamic> item) {
        provider.save(newItem: item);
        onFinish.call(false);
      },
    );
  }

  @override
  String get displayName => 'HOST切换';

  @override
  ImageProvider<Object> get iconImageProvider => MemoryImage(envIconBytes);

  @override
  String get name => 'ServiceEnvPlugin';//

  @override
  void onTrigger() {

  }

}